@js:

content={
    childNode:[],
    childNodes:java.getElements(".content@p").toArray(),
    UpWz:function(m, i) {
        var k = Math.ceil((i + 1) % this.code);
        k = Math.ceil(m - k);
        return k
    },
    load:function(){
  this.code=result.match(/codeurl=.(\d+)/)[1];
        var e =String(java.base64Decode(java.getString('[name=client]@content',!1))).split(/[A-Z]+%/);

        for (var i = 0; i < e.length; i++) {
            var k = this.UpWz(e[i], i);
            this.childNode[k]=this.childNodes[i]
        }}
}


if((/codeurl/).test(result)){
content.load();
result=content.childNode.join("<br>");}

if(baseUrl.match(/novel\/\d+\.html/)){
result="暂不支持本书目录解析,请到浏览器阅读"}

